import React from 'react';

import { Col, Card, CardTitle, CardHeader, CardBody } from 'reactstrap';
import { Container } from 'reactstrap';
import { Row } from 'reactstrap';
import PieChart from './All Devices';
import Select from 'react-dropdown-select';

import TableUI from '../../components/TableUI/TableUI';
import UnderConstruction from "../../components/UnderConstruction/UnderConstruction";

import axios from 'axios';


import '../../assets/scss/layout.scss';
import '../../assets/scss/card.scss';
import { HelpOutline, ThreeSixtyOutlined } from '@material-ui/icons';

const options = [
    { label: "All Devices", value: 3 },
    { label: "Desktops", value: 2 },
    { label: "Laptops", value: 1 }
  ];



class DeviceMonitoring extends React.Component {
    state = {
        options: [
            {
            name: 'Select…',
            value: null,
            },
            {
            name: 'All Devices',
            value: 'All Devices',
            },
            {
            name: 'Desktops',
            value: 'Desktops',
            },
            {
            name: 'Laptops',
            value: 'Laptops',
            },
        ],
        value: 'All Devices',
        };

    handleChange = (event) => {
        this.setState({ value: event.target.value});
    };

    render() {
        const { options, values } = this.state;
        return (
            <div className="mainSection">
                <Container>
                    <Row className="row-2">
                        <Col className="col-12">
                            <Card className="contact-block">
                            <select onChange={this.handleChange} value={value}>
                                {options.map(item => (
                                    <option key={item.value} value={item.value}>
                                    {item.name}
                                    </option>
                                ))}
                                </select>
                                <CardHeader>
                                    {value}
                                </CardHeader>
                                <PieChart/>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default DeviceMonitoring;
